<pre><code>        private void init_map(object sender, EventArgs e)
        {           
            mapcon1.MapProvider = GMap.NET.MapProviders.GoogleMapProvider.Instance;
            mapcon1.Position = new PointLatLng(14.076412, 100.30723);
     //     mapcon1.SetCurrentPositionByKeywords(&quot;Bangkok&quot;);
            mapcon1.MinZoom = 3;
            mapcon1.MaxZoom = 17;
            mapcon1.Zoom = 13;
            mapcon1.Manager.Mode = AccessMode.ServerOnly;

            markerOverlays = new GMapOverlay(mapcon1, &quot;overlay&quot;);
            markerOverlays.Markers.Add(new GMap.NET.WindowsForms.Markers.GMapMarkerGoogleRed(new PointLatLng(14.076412, 100.30723)));
            mapcon1.Overlays.Add(markerOverlays);
            markerOverlays.Markers.Add(new GMap.NET.WindowsForms.Markers.GMapMarkerGoogleRed(new PointLatLng(14.078811,100.309869)));
            mapcon1.Overlays.Add(markerOverlays);

            //Show path
            PointLatLng start = new PointLatLng(14.076412, 100.30723);
            PointLatLng end = new PointLatLng(14.078811, 100.309869);

            MapRoute route = GMap.NET.MapProviders.GoogleMapProvider.Instance.GetRouteBetweenPoints(start,end, true, false, 13);
            GMapRoute r = new GMapRoute(route.Points, &quot;r&quot;);

            GMapOverlay routeOverlay = new GMapOverlay(mapcon1,&quot;routeOverlay&quot;);
            routeOverlay.Routes.Add(r);
            mapcon1.Overlays.Add(routeOverlay);

        }</code></pre>
