                markersOverlay.Markers.Add(marker);
            }
            else
            {
                markersOverlay.Markers.Add(marker);
                mapExplorer.Overlays.Add(markersOverlay);
            }

            mapExplorer.Position = new PointLatLng(lat, lon);

